import frappe

@frappe.whitelist()
def create_edu_fees_for_students(student_group):
    for student in student_group:
        fee = frappe.get_doc({
            "doctype": "Edu Fees",  # تأكد من أن هذا هو الـ Doctype الذي تريد إنشاءه
            "student": student,     # الطالب
            "amount": 1000,         # المبلغ (يمكن تغييره حسب متطلباتك)
        })
        fee.insert()

@frappe.whitelist()
def on_after_insert(doc, method):
    # هذه هي الدالة التي سيتم استدعاؤها بعد الإدراج
    student_group = doc.students  # افترض أن الطلاب مخزنين هنا
    create_edu_fees_for_students(student_group)  # استدعاء الدالة الخاصة بإنشاء الرسوم
